dateFormat.i18n = {
    dayNames: ["Пн", "Вт", "Ср", "Чт", "Пт", "Сб", "Вс", "Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота", "Воскресенье"],
    monthNames: ["Янв", "Фев", "Мар", "Апр", "Май", "Июн", "Июл", "Авг", "Сен", "Окт", "Ноя", "Дек", "Январь", "Февраль", "Март", "Апрель", "Май", "Июнь", "Июль", "Август", "Сентябрь", "Октябрь", "Ноябрь", "Декабрь"]
};

(function() {
    function numpf(n, f, s, t) {
        // f - 1, 21, 31, ...
        // s - 2-4, 22-24, 32-34 ...
        // t - 5-20, 25-30, ...
        var n10 = n % 10;
        if ((n10 == 1) && ((n == 1) || (n > 20))) {
            return f;
        } else if ((n10 > 1) && (n10 < 5) && ((n > 20) || (n < 10))) {
            return s;
        } else {
            return t;
        }
    }

    jQuery.timeago.settings.strings = {
        prefixAgo: null,
        prefixFromNow: "через",
        suffixAgo: "назад",
        suffixFromNow: null,
        seconds: "меньше минуты",
        minute: "минуту",
        minutes: function(value) {
            return numpf(value, "%d минута", "%d минуты", "%d минут");
        },
        hour: "час",
        hours: function(value) {
            return numpf(value, "%d час", "%d часа", "%d часов");
        },
        day: "день",
        days: function(value) {
            return numpf(value, "%d день", "%d дня", "%d дней");
        },
        month: "месяц",
        months: function(value) {
            return numpf(value, "%d месяц", "%d месяца", "%d месяцев");
        },
        year: "год",
        years: function(value) {
            return numpf(value, "%d год", "%d года", "%d лет");
        }
    };
})(); 
